package com.mauriciotogneri.trail;

import com.mauriciotogneri.trail.Trail.LogLevel;

/**
 * Represents a log generated by the application.
 */
public class TrailLog
{
    /**
     * The log level.
     */
    public final LogLevel level;

    /**
     * The code location.
     */
    public final CodeLocation location;

    /**
     * The tag.
     */
    public final String tag;

    /**
     * The message.
     */
    public final String message;

    /**
     * The exception (can be null).
     */
    public final Throwable exception;

    TrailLog(LogLevel level, CodeLocation location, String tag, String message, Throwable exception)
    {
        this.level = level;
        this.location = location;
        this.tag = tag;
        this.message = message;
        this.exception = exception;
    }

    /**
     * Returns true if the log contains an exception.
     *
     * @return true if the log contains an exception
     */
    public boolean hasException()
    {
        return (this.exception != null);
    }
}